package Kyus5;import java.util.ArrayList;import java.util.List;public class ValidParentheses {/** Write a function that takes a string of parentheses, and determines if the order of the parentheses is valid. The function should return true if the string is valid, and false if it's invalid.Examples"()"              =>  true")(()))"          =>  false"("               =>  false"(())((()())())"  =>  true* */    public static boolean validParentheses(String parens)    {        List<Character> myList = new ArrayList<Character>();        for (Character c : parens.toCharArray()) {            if (c == '(') {                myList.add(c);            } else if (c == ')') {                if (myList.isEmpty() || myList.get(myList.size()-1) != '(') {                    return false;                }                myList.remove(myList.size()-1);            }        }        return myList.isEmpty();    }}